(defn reset-randomizer [n] (alter-var-root #'r (constantly (java.util.Random. n))))

(alter-var-root #'clojure.core/rand (constantly (fn ([] (.nextDouble user/r)) ([n] (* n (clojure.core/rand))))))

(defn coin [] (rand-nth [:H :T])) ; #'user/coin

